def get_epoch_vs_iou_results(results, iou_type='train', iou_th_idx=2):
    """
     From the collected results retrieve the epoch vs iou results

    :param results:
    :param iou_type:
    :param iou_th_idx:
    :return:  array of [# epoch, loss, iou]
    """
    epoch_iou_array = np.zeros((len(results), 3))  # epoch, loss, iou

    if iou_type.lower() == 'train':
        iou_type_idx = 2
        loss_idx = 1
    elif iou_type.lower() == 'val':
        iou_type_idx = 4
        loss_idx = 3
    else:
        raise Exception("Invalid iou Type {}!".format(iou_type))

    for e_idx, epoch_results in enumerate(results):
        epoch_iou_array[e_idx, 0] = epoch_results[0]
        epoch_iou_array[e_idx, 1] = epoch_results[loss_idx]
        epoch_iou_array[e_idx, 2] = epoch_results[iou_type_idx][iou_th_idx]

    return epoch_iou_array


def plot_iou_over_time(data, iou_type='train', iou_th_idx=2, axis=None, label=None):
    """
    thresh_idx =
        0 : 0.1,
        1 : 0.2,
        2 : 0.3,
        3 : 0.4,
        4 : 0.5,
        5 :  0.6,
        6 : 0.7,
        7 : 0.8

    :param label:
    :param data:
    :param iou_type:
    :param iou_th_idx:
    :param axis:
    :return:
    """

    res = get_epoch_vs_iou_results(data['training'], iou_type=iou_type, iou_th_idx=iou_th_idx)
    # returns an array of [epoch, loss, iou]

    if axis is None:
        f, axis = plt.subplots()

    axis.plot(res[:, 0], res[:, 2], label=label)


def plot_all_results_in_result_list(res_list, iou_th_idx=2, axis_arr=None, label=None):
    if axis_arr is None:
        f, axis_arr = plt.subplots(1, 2, figsize=(15, 7))

    for res_idx, res in enumerate(res_list):
        plot_iou_over_time(res, 'train', iou_th_idx=iou_th_idx, axis=axis_arr[0], label=label+'_seed_{}'.format(res['seed']))
        plot_iou_over_time(res, 'val', iou_th_idx=iou_th_idx, axis=axis_arr[1], label=label+'_seed_{}'.format(res['seed']))

    axis_arr[0].set_xlabel("Epoch")
    axis_arr[0].set_ylabel("IoU")
    axis_arr[0].legend()
    axis_arr[0].set_title("Train")
    axis_arr[0].grid('ON')

    axis_arr[1].set_xlabel("Epoch")
    axis_arr[1].set_ylabel("IoU")
    # axis_arr[0].legend()
    axis_arr[1].set_title("Val")
    axis_arr[1].grid('ON')


